          data-toggle="modal" data-target="#exampleModal"
          onClick={handleDeleteAll}>
            DELETE ALL
          </button>
        </div>
      }
</div>
</>
)
}
export async function getServerSideProps() {
  
 
   const res = await getData('product')
 
   
   return {
     props: {
      products: res.products,
      result: res.result
     
     }, 
   }
 }
export default Home
